SELECT r.`id`, r.`app_id`, r.`request_path`, r.`request_type`, r.`java_class`, r.`short_method`, r.`method`, ro.`id` `role_id`, ro.`role_name` FROM `sys_resource` AS r INNER JOIN `sys_role_resource` AS rr ON rr.`resources_id`=r.`id` AND rr.`type`=1 INNER JOIN `sys_role` AS ro ON ro.`id`=rr.`role_id`
r.`method` = #{queryKey,jdbcType=VARCHAR}
r.`resource_path` = #{queryKey,jdbcType=VARCHAR}
r.`resource_path` = #{queryKey,jdbcType=VARCHAR}
AND ro.`enable`=1 AND r.`enable`=1

SELECT r.`id`, r.`request_path`, r.`request_type`, r.`java_class`, r.`short_method`, r.`method`, ro.`id` `role_id`, ro.`role_name` FROM `sys_resource_group` AS rg INNER JOIN `sys_resource_group_relation` AS rgr ON rgr.`resource_group_id`=rg.`id` INNER JOIN `sys_resource` AS r ON rgr.`resources_id`=r.`id` INNER JOIN `sys_role_resource` AS rr ON rr.`resources_id`=rg.`id` AND rr.`type`=2 INNER JOIN `sys_role` AS ro ON ro.`id`=rr.`role_id`
r.`method` = #{queryKey,jdbcType=VARCHAR}
r.`resource_path` = #{queryKey,jdbcType=VARCHAR}
r.`resource_path` = #{queryKey,jdbcType=VARCHAR}
AND ro.`enable`=1 AND r.`enable`=1 AND rg.`enable`=1
