SELECT u.`id`, u.`user_name`, u.`password`, u.`mobile`, u.`email`, u.`enable`, u.`is_lock`, u.`expire`, r.`id` `role_id`, r.`role_name` FROM `sys_user` AS u INNER JOIN `sys_user_role` ur ON ur.`user_id` =u.`id` INNER JOIN `sys_role` AS r ON ur.`role_id`=r.`id`
u.`user_name` = #{queryKey,jdbcType=VARCHAR}
u.`mobile` = #{queryKey,jdbcType=VARCHAR}
u.`email` = #{queryKey,jdbcType=VARCHAR}
u.`user_name` = #{queryKey,jdbcType=VARCHAR}
AND u.`enable`=1 AND r.`enable`=1
